        for kappa_ in range(kappa+_sage_const_1 ):
            M_log = kappa_*H(CB3)+_sage_const_1  # number of CVP-targets
            beta = find_beta(dim-kappa_, n-kappa_, q, st_dev_e)
            if beta==infinity: continue
            Tbkz = svp_cost(beta,dim-kappa_)
            _, Tcvp = batchCVPP_cost(beta, M_log, sqrt(_sage_const_4 /_sage_const_3p ), _sage_const_1 )
            min_ = max(Tbkz, Tcvp)
